import vue from 'vue';
import vuex from 'vuex';
vue.use(vuex);



export default new vuex.Store({
    state: {
        name: sessionStorage.getItem('uname') || ""
    },
    mutations: {
        setname: (state, value) => {
            state.name = value;
            sessionStorage.setItem('uname', state.name);
        }
    }
});
